import { useRef, useState } from 'react';

import classes from './Checkout.module.css';
import Modal from '../UI/Modal';
import Invvice from '../invoice/Invoice';

const Checkout = (props) => {
  // const data = [
  //   {
  //     onClose: props.onClose,
  //     name: props.name,
  //     amount: props.amount,
  //     price: props.price,
  //   }
  // ];
  const [hasInvvice, setHasInvvice] = useState(false);
  const [formIsValet, steFormIsValet] = useState({
    phoneNumber: true,
    street: true,
    postal: true,
    city: true,
  })
  const phoneNumberInputRef = useRef();
  const streetInputRef = useRef();
  const postalInputRef = useRef();
  const cityInputRef = useRef();

  const isFiveCharacter = (value) => value.trim().length === 5;
  const isTeanCharacter = (value) => value.trim().length === 10;
  const isEmpty = (value) => value.trim().length === 0;


  const confirmHandler = (event) => {
    event.preventDefault();
    const enterdPhoneNumber = phoneNumberInputRef.current.value;
    const enterdStreet = streetInputRef.current.value;
    const enterdPostal = postalInputRef.current.value;
    const enterdCity = cityInputRef.current.value;

    const ValetEntredPhoneNumber = isTeanCharacter(enterdPhoneNumber);
    const ValetEntredStreet = !isEmpty(enterdStreet);
    const ValetEntredPostal = isFiveCharacter(enterdPostal);
    const ValetEntredCity = !isEmpty(enterdCity);

    steFormIsValet({
      phoneNumber: ValetEntredPhoneNumber,
      street: ValetEntredStreet,
      postal: ValetEntredPostal,
      city: ValetEntredCity,
    })

    if (formIsValet.phoneNumber && formIsValet.street && formIsValet.postal && formIsValet.city) {
      props.onSubmitOrder({
        phoneNumber: enterdPhoneNumber,
        street: enterdStreet,
        city: enterdCity,
        postalCode: enterdPostal,
      })
      setHasInvvice(true);

      console.log(props.name)
      return;
    }
    else
      console.log("formIsNotValet")

  };
  // const ranerInv = (
  //   <Invvice
  //     onCancel={props.onClose}
  //     name={props.name}
  //     amount={props.amount}
  //     price={props.price}
  //   />
  // );

  const moduleAchean = (
    <div className={classes.actions}>
      <button type='button' onClick={props.onCancel}>
        Cancel
      </button>
      <button className={classes.submit}>Confirm</button>
    </div>);

  const phoneNumberControlClass = `${classes.control} ${formIsValet.phoneNumber ? '' : classes.invalid
    }`;
  const streetControlClass = `${classes.control} ${formIsValet.street ? '' : classes.invalid
    }`;
  const postalControlClass = `${classes.control} ${formIsValet.postal ? '' : classes.invalid
    }`;
  const cityControlClass = `${classes.control} ${formIsValet.city ? '' : classes.invalid
    }`;

  return (
    <Modal onClose={props.onCancel}>
      <form className={classes.form} onSubmit={confirmHandler}>
        <div className={phoneNumberControlClass}>
          <label htmlFor='phone'>Your Phone Number</label>
          <input type='number' id='phone' ref={phoneNumberInputRef} />
          {!formIsValet.phoneNumber && <p>pleass enter valid phone number(10 digat)!!</p>}
        </div>
        <div className={streetControlClass}>
          <label htmlFor='street'>Street</label>
          <input type='text' id='street' ref={streetInputRef} />
          {!formIsValet.street && <p>pleass enter valid street!</p>}
        </div>
        <div className={postalControlClass}>
          <label htmlFor='postal'>Postal Code</label>
          <input type='text' id='postal' ref={postalInputRef} />
          {!formIsValet.postal && <p>pleass enter valid postal code(5 character)!!</p>}
        </div>
        <div className={cityControlClass}>
          <label htmlFor='city'>City</label>
          <input type='text' id='city' ref={cityInputRef} />
          {!formIsValet.city && <p>pleass enter valid city!</p>}

          {/* {hasInvvice && ranerInv} */}
          {/* {!hasInvvice && moduleAchean} */}
          {moduleAchean}

        </div>

      </form>
    </Modal>
  );
};

export default Checkout;
