const AvailableMeals = () => {
  const [meals, setMeals] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMeals = async () => {
      const response = await fetch('https://react-http-de91a-default-rtdb.firebaseio.com/meals.json');
      const mealsResponse = await response.json();

      const lodedMeals = [];
      for (const key in mealsResponse) {
        lodedMeals.push({
          id: key,
          name: mealsResponse[key].name,
          description: mealsResponse[key].description,
          price: mealsResponse[key].price
        });
      }
      setLoading('false');
      setMeals(lodedMeals);
    };
    fetchMeals();
  }, [])

  const mealsList = meals.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
